CREATE TABLE "public"."item_image" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "user_id" uuid NOT NULL, "file_id" uuid NOT NULL, "beer_id" uuid, "wine_id" uuid, "spirit_id" uuid, "is_public" boolean NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "auth"."users"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("file_id") REFERENCES "storage"."files"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("beer_id") REFERENCES "public"."beers"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("wine_id") REFERENCES "public"."wines"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("spirit_id") REFERENCES "public"."spirits"("id") ON UPDATE restrict ON DELETE restrict, CONSTRAINT "Ensure at least one item Id" CHECK (num_nonnulls(beer_id, wine_id, spirit_id) = 1));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
