CREATE TABLE "public"."item_favorites" ("id" uuid NOT NULL, "user_id" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "beer_id" uuid, "wine_id" uuid, "spirit_id" uuid, "coffee_id" uuid, PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "auth"."users"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("beer_id") REFERENCES "public"."beers"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("wine_id") REFERENCES "public"."wines"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("spirit_id") REFERENCES "public"."spirits"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("coffee_id") REFERENCES "public"."coffees"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("user_id", "beer_id", "wine_id", "spirit_id", "coffee_id"), CONSTRAINT "Ensure one item_id present" CHECK (num_nonnulls(beer_id, wine_id, spirit_id, coffee_id) = 1));
